export const curPosState = {
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
}

export const curPosReducer = (state, action) => {
    switch(action.type) {
        case 'increment':
            return { ...state, value1: state.value1 + 1 };
        case 'decrement':
            return { ...state, value1: state.value1 - 1 };
        default:
            return state;
    }
}
