import { createContext, useReducer } from "react";

export const TestContext2 = createContext();

const initialState = {
    value2: 0,
}

const reducer = (state, action) => {
    switch(action.type) {
        case 'increment':
            return { ...state, value2: state.value2 + 1 };
        case 'decrement':
            return { ...state, value2: state.value2 - 1 };
        default:
            return state;
    }
}

export function TestManager2({ children }) {
    const [state, dispatch] = useReducer(reducer, initialState);


    return <TestContext2.Provider
        value={{ value2: state.value2, dispatch2: dispatch }}
    >
        { children }
    </TestContext2.Provider>
}
