import { createContext, useContext } from "react";
import { pickFromObj } from "../util";

// An object that contains all contexts for state management
export const contexts = {
    CurPosContext: createContext(),
    MovementContext: createContext(),
}

/**
 * Pass Context params to component as props
 * @param {Object} WrappedComponent     react element
 * @param {String} contextKey           key of the target context
 * @param {} properties 
 * @returns 
 */
export function connectToContext(WrappedComponent, select){
    return function(props){
      const contextProps = useFilteredContext(contextKey, properties);
      return <WrappedComponent {...contextProps} {...props}/>
    }
}
