import { useContext, useReducer } from "react";
import { reducer1, reducer2, initialState } from "../../lib/reducers";
import { TestContext } from "../../lib/stateManagement/TestManager";

export function Item() {
    const { value1, dispatch } = useContext(TestContext);
    return <div>
        <p>{ value1 }</p>
        <button onClick={() => dispatch({ type: 'increment' })}>increment</button>
        <button onClick={() => dispatch({ type: 'decrement' })}>decrement</button>
    </div>
}
