export const initialState = {
    value1: 0,
    value2: 0,
}

export function reducer1(state, action) {
    switch(action.type) {
        case 'increment':
            return { ...state, value1: state.value1 + 1 };
        case 'decrement':
            return { ...state, value2: state.value1 - 1};
        default:
            return state;
    }
}

export function reducer2(state, action) {
    switch(action.type) {
        case 'increment':
            return { ...state, value2: state.value2 + 1 };
        case 'decrement':
            return { ...state, value2: state.value2 - 1 };
        default:
            return state;
    }
}
