export const val1State = {
    value1: 0,
}

export const val1Reducer = (state, action) => {
    switch(action.type) {
        case 'increment':
            return { ...state, value1: state.value1 + 1 };
        case 'decrement':
            return { ...state, value1: state.value1 - 1 };
        default:
            return state;
    }
}
