import { createContext, useContext } from "react";
import { pickFromObj } from "../util";

// An object that contains all contexts for state management
export const contexts = {
    Val1Context: createContext(),
    Val2Context: createContext(),
}

/**
 * A hook that obtain a Context or parts of it
 * @param {String} context          identifier of the context
 * @param {Array.<String>} keys     keys of the targeted part of context
 * @returns values of the context
 */
export function filterContext(context, keys = []) {
    if(!context && typeof context !== 'string') return {};
    const contextObj = useContext(contexts[context]);
    if(!Array.isArray(keys) || keys.length === 0) return contextObj;
    return pickFromObj(contextObj, keys);
}
