import { useRef, cloneElement } from 'react';
import Draggable from 'react-draggable';

export function ResizeHandler(props) {
    const {
        position,
        restingClassName,
        draggingClassName,
        resizeStart,
        resize,
        resizeEnd,
        bounds,
        handlerComponent
    } = props;
    return <Draggable
        //bounds={bounds}
        //position={position}
        onStart={resizeStart}
        onDrag={resize}
        onStop={resizeEnd}
        defaultClassName={restingClassName}
        draggingClassName={draggingClassName}
    >
        {cloneElement(React.Children.only(handlerComponent), {
        })}
    </Draggable>
}
