export const val2State = {
    value2: 0,
}

export const val2Reducer = (state, action) => {
    switch(action.type) {
        case 'increment':
            return { ...state, value2: state.value2 + 1 };
        case 'decrement':
            return { ...state, value2: state.value2 - 1 };
        default:
            return state;
    }
}
