import * as React from "react";
import { useContext, useEffect, useReducer, memo, cloneElement, useState } from "react";
import Draggable from "react-draggable";
import { contexts } from "../../lib/stateManagement/contexts";
import { connectToContext } from "../../lib/stateManagement/contexts";

/**
 * Retrieve props from context
 * @returns {Object} props for Item
 */
const useContextProps = () => {
    const curPosProps = useContext(contexts['CurPosContext']);
    const movementProps = useContext(contexts['MovementContext']);
    return { ...curPosProps, ...movementProps };
}

export const Item = connectToContext(memo(props => {
    const {
        children,
        initialX,
        initialY,
        curPosState
    } = props

    const [x, setX] = useState(initialX || 0);
    const [y, setY] = useState(initialY || 0);
    const dragStart = (e, data) => {
    }
    const drag = (e, data) => {
        console.log(curPosState);
        setX(data.x);
        setY(data.y);
    }
    const dragEnd = (e, data) => {
    }

    return <>
        <Draggable
            bounds='parent'
            axis="both"
            position={{x: x, y: y}}
            onStart={dragStart}
            onDrag={drag}
            dragEnd={dragEnd}
        >
            {cloneElement(React.Children.only(children), {
                /*className: className,
                style: {...children.props.style, ...style},
                transform: svgTransform*/
            })}
        </Draggable>
    </>
}), useContextProps);
