import { createContext, useContext } from "react";
import { pickFromObj } from "../util";

// An object that contains all contexts for state management
export const contexts = {
    CurPosContext: createContext(),
    MovementContext: createContext(),
}

/**
 * A hook that obtains a Context or parts of it
 * @param {String} context          identifier of the context
 * @param {Array.<String>} keys     keys of the targeted part of context
 * @returns values of the context
 */
export function useFilteredContext(context, keys = []) {
    const contextObj = useContext(contexts[context]);
    if(!context && typeof context !== 'string') return {};
    if(!Array.isArray(keys) || keys.length === 0) return contextObj;
    return pickFromObj(contextObj, keys);
}

/**
 * Pass Context params to component as props
 * @param {Object} WrappedComponent     react element
 * @param {String} contextKey           key of the target context
 * @param {} properties 
 * @returns 
 */
export function connectToContext(WrappedComponent, contextKey, properties = []){
    return function(props){
      const contextProps = useFilteredContext(contextKey, properties);
      return <WrappedComponent {...contextProps} {...props}/>
    }
}
